import jsTPS_Transaction from "../common/jsTPS.js";
/**
 * AddSong_Transaction
 *
 * This class represents a transaction that works with adding a song. It will be managed by the transaction stack.
 *
 * @author McKilla Gorilla
 * @author ?
 */

export default class EditSong_Transaction extends jsTPS_Transaction {
  constructor(initStore, initIndex) {
    super();
    this.store = initStore;
    this.index = initIndex;
    this.oldTitle = this.store.currentList.songs[this.index].title;
    this.oldArtist = this.store.currentList.songs[this.index].artist;
    this.oldYoutube = this.store.currentList.songs[this.index].youTubeId;

    this.title = document.getElementById("titleBox").value;
    this.artist = document.getElementById("artistBox").value;
    this.youTubeId = document.getElementById("youtubeBox").value;
  }

  doTransaction() {
    this.store.edit(this.index, this.title, this.artist, this.youTubeId);
  }

  undoTransaction() {
    this.store.editSong(this.index,this.oldTitle,this.oldArtist,this.oldYoutube);
  }
}
