import React, { useContext } from "react";
import { GlobalStoreContext } from "../store";

const DeleteSongModal = () => {
  const { store } = useContext(GlobalStoreContext);
  let cl = store.currentList;

  if (!cl){
      return null;
  } 

  let i = store.songKeyMarked;
  let title = index === undefined ? "" : cl.songs[index].title;

  return (
    <div
      className="modal"
      id="remove-song-modal"
      data-animation="slideInOutLeft"
    >
      <div className="modal-root" id="verify-remove-song-root">
        <div className="modal-north">Remove Song?</div>
        <div className="modal-center">
          <div className="modal-center-content">
            Are you sure you wish to permanently remove {title} from the
            playlist?
          </div>
        </div>
        <div className="modal-south">
          <input
            type="button"
            id="remove-song-confirm-button"
            className="modal-button"
            onClick={() => store.deleteSong(index)}
            value="Confirm"
          />
          <input
            type="button"
            id="remove-song-cancel-button"
            className="modal-button"
            onClick={store.hideDeleteSongModal}
            value="Cancel"
          />
        </div>
      </div>
    </div>
  );
};

export default DeleteSongModal;
