import jsTPS_Transaction from "../common/jsTPS.js"
/**
* MoveSong_Transaction
*
* This class represents a transaction that works with drag
* and drop. It will be managed by the transaction stack.
*
* @author McKilla Gorilla
* @author ?
*/
export default class EditSong_Transaction extends jsTPS_Transaction {
   constructor(initstore,initIndex,initTitle,newTitle,initArtist,newArtist,initYoutube,newYoutube) {
       super();
       this.store = initstore;
       this.index = initIndex;
       this.OldTitle = initTitle;
       this.OldArtist = initArtist;
       this.OldYoutube=initYoutube;  
       this.title = newtitle;
       this.artist = newArtist;
       this.youtube=newYoutube;  
   }

   doTransaction() {
       this.store.editSongfunc(this.index,this.title,this.artist,this.youtube);
   }

   undoTransaction() {
       this.store.editSongfunc(this.index,this.OldTitle,this.OldArtist,this.OldYoutube);
   }
}
