import { useContext, useState } from 'react'
import { useHistory } from 'react-router-dom'
import { GlobalStoreContext } from "../store";

function EditSongModal() {
  const { store } = useContext(GlobalStoreContext);
  store.history = useHistory();

  function handleEditSong() {
    let title = document.getElementById("title");
    let artist = document.getElementById("artist");
    let youtubeid = document.getElementById("youtubeid");
    let newSong = {
      title: title.value,
      artist: artist.value,
      youTubeId: youtubeid.value,
    };
    title.value = "";
    artist.value = "";
    youtubeid.value = "";
    store.editMarkedSong(newSong);
  }

  function closeEditSongModal() {
    store.closeEditSongModal();
  }

  return (
    <div class="modal" id="edit-song-modal" data-animation="slideInOutLeft">
      <div class="modal-root" id="verify-edit-song-root">
        <div class="modal-north">Edit Song</div>
        <div class="modal-center">
          <div class="modal-center-content">
            <label class="preText">Title: </label>
            <input
              type="text"
              id="titleBox"
              class="textBox"
              onClick={this.select}
            />
            <br />
            <label class="preText">Artist: </label>
            <input
              type="text"
              id="artistBox"
              class="textBox"
              onClick={this.select}
            />
            <br />
            <label class="preText">YouTube ID:</label>
            <input
              type="text"
              id="youtubeBox"
              class="textBox"
              onClick={this.select}
            />
            <br />
          </div>
        </div>
        <div class="modal-south">
          <input
            type="button"
            id="edit-song-confirm-button"
            class="modal-button"
            onClick={handleEditSong}
            value="Confirm"
          />
          <input
            type="button"
            id="edit-song-cancel-button"
            class="modal-button"
            onClick={closeEditSongModal}
            value="Cancel"
          />
        </div>
      </div>
    </div>
  );
}

export default EditSongModal;
