import { useContext, useState } from 'react'
 import { useHistory } from 'react-router-dom'
 import { GlobalStoreContext } from '../store'

 function EditSongModal () {
     const { store } = useContext(GlobalStoreContext);
     let index = store.markEditSongIndex;

     function editSongCancel() {
         store.hideEditSongModal();
     }

     function editSongConfirm() {
         store.edit(index);
         store.hideEditSongModal();
     }

  return (
    <div class="modal" id="edit-song-modal" data-animation="slideInOutLeft">
      <div class="modal-root" id="verify-edit-song-root">
        <div class="modal-north">Edit playlist</div>
        <div class="modal-center">
          <div class="modal-center-content">
            <label class="preText">Title: </label>
            <input
              type="text"
              id="titleBox"
              class="textBox"
              onClick={this.select}
            />
            <br />
            <label class="preText">Artist: </label>
            <input
              type="text"
              id="artistBox"
              class="textBox"
              onClick={this.select}
            />
            <br />
            <label class="preText">YouTube ID:</label>
            <input
              type="text"
              id="youtubeBox"
              class="textBox"
              onClick={this.select}
            />
            <br />
          </div>
        </div>
        <div class="modal-south">
          <input
            type="button"
            id="edit-song-confirm-button"
            class="modal-button"
            onClick={editSongConfirm}
            value="Confirm"
          />
          <input
            type="button"
            id="edit-song-cancel-button"
            class="modal-button"
            onClick={editSongCancel}
            value="Cancel"
          />
        </div>
      </div>
    </div>
  );
}

export default EditSongModal;
